// src/App.js
import './App.css';
// src/App.js (Updated)
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Product from './components/ProductList';
import Cart from './components/Cart';
import ProductDetails from './components/ProductDetails';

const App = () => {
  const [cart, setCart] = useState([]);

  const products = [
    { id: 1, name: 'Product 1', price: 29.99, image: 'https://via.placeholder.com/150' },
    { id: 2, name: 'Product 2', price: 39.99, image: 'https://via.placeholder.com/150' },
    { id: 3, name: 'Product 3', price: 19.99, image: 'https://via.placeholder.com/150' },
  ];

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (productToRemove) => {
    setCart(cart.filter(item => item.id !== productToRemove.id));
  };

  const handleCheckout = () => {
    const total = cart.reduce((acc, item) => acc + item.price, 0).toFixed(2);
    alert(`Your total is $${total}. Thank you for your purchase!`);
    setCart([]); // Clear the cart after checkout
  };

  return (
    <Router>
      <div className="container">
        <h1 className="my-4">Product Catalog</h1>
        <Switch>
          <Route path="/product/:id" render={({ match }) => {
            const product = products.find(p => p.id === parseInt(match.params.id));
            return product ? <ProductDetails product={product} /> : <h2>Product not found</h2>;
          }} />
          <Route path="/">
            <div className="row">
              {products.map(product => (
                <Product key={product.id} product={product} addToCart={addToCart} />
              ))}
            </div>
            <Cart cart={cart} removeFromCart={removeFromCart} />
            {cart.length > 0 && (
              <button onClick={handleCheckout} className="btn btn-success my-4">
                Checkout
              </button>
            )}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
