// src/App.js
import './App.css';
// src/App.js (Updated)
// src/App.js (Updated)
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Product from './components/ProductList';
import Cart from './components/Cart';

const App = () => {
  const [cart, setCart] = useState([]);

  const products = [
    { id: 1, name: 'Product 1', price: 29.99, image: 'path/to/image1.jpg' },
    { id: 2, name: 'Product 2', price: 39.99, image: 'path/to/image2.jpg' },
    { id: 3, name: 'Product 3', price: 19.99, image: 'path/to/image3.jpg' },
    // Add more products as needed
  ];

  const addToCart = (product) => {
    setCart([...cart, product]);
    console.log('Cart:', [...cart, product]); // For debugging
  };

  return (
    <Router>
      <div className="container">
        <h1 className="my-4">Product Catalog</h1>
        <div className="row">
          {products.map(product => (
            <Product key={product.id} product={product} addToCart={addToCart} />
          ))}
        </div>
        <Cart cart={cart} />
      </div>
    </Router>
  );
};

export default App;
